// SAR RGB False Color Render by EOX
// red HH[dB] (-20 to 0 [dB]stretch)
// green HV[dB] (-25 to -5 [dB]stretch)
// blue HH[dB]-HV[dB] (0 to 15 [dB]stretch)

var hh_eox_db = 10*Math.log10(HH);
var hv_eox_db = 10*Math.log10(HV);

var red = Math.max(0, (hh_eox_db + 20) / 20);
var green = Math.max(0, (hv_eox_db + 25) / 20);
var blue = Math.max(0, (hh_eox_db-hv_eox_db) / 15);

return[red, green, blue];

// EOX Sentinel-1 HH Greyscale image
